function strfunction(){
    var word="I Am Angular Developer";
    var lengthOne=word.length;
    var res=word.split(" ").length;
    var divres=lengthOne/res;
    var mulres=lengthOne*res;
    console.log(`The length of string:- "${lengthOne}"`);
    console.log(`Divided by total no. of word:- "${res}"`);
    console.log(`Result:- "${divres}"`);
    console.log(`The length of string:- "${lengthOne}"`);
    console.log(`Multiply by total no. of word:- "${res}"`);
    console.log(`Result:- "${mulres}"`);
    
    
    
    

}
