

function isEven(num){
console.log(`before if block`);
if (num%2==0) {
    console.log(`Even no.`);
    
}
console.log(`After if block`);
}
isEven(10);
isEven(9);
isEven(22);
isEven(1045);
console.log(`==================if-else==================================================================`);
var isEvenOrOdd=function(num){
    if (num%2==0) {
        console.log(`EVEN`);
    } else {
        console.log(`ODD`);
    }

}
isEvenOrOdd(3);
isEvenOrOdd(10);



