console.log(`=============WAP to print number from 0 to 10==================`);
var i=0; //Initial expression
while (i<=10) { // Condition 
    console.log(i);
    i++; //Update Expression
    
}

console.log(`=============WAP to print number from 5 to 10==================`);
var i=5;
do {
    console.log(i);
    i++;
} while (i<=10);